mapper.insert:
INSERT INTO sm_commodity (commodity_name, commodity_price, is_discount, discount_start_at,
discount_end_at, commodity_bar_code) VALUES (#{commodityName}, #{commodityPrice}, #{
isDiscount}, #{discountStartAt}, #{discountEndAt}, #{commodityBarCode})

mapper.update:
UPDATE sm_commodity SET commodity_name = #{commodityName}, commodity_price = #{commodityPrice},

    is_discount = #{isDiscount},
    discount_start_at = #{discountStartAt},
    discount_end_at = #{discountEndAt}

commodity_bar_code = #{commodityBarCode} WHERE commodity_id = #{commodityId}
